 function validateSecurity() {
    var tot = Number_Rows_Grid("security", "secu_type");
  var secu_type, secu_detl, valuer, val_date, secu_value_fsv, secu_value_mkt;
    for (var i = 1; i <= tot; i++) {
      secu_type = getGridField("security", i, "secu_type").value;
      secu_detl = getGridField("security", i, "secu_detl").value;
      valuer = getGridField("security", i, "valuer").value;
      val_date = getGridField("security", i, "val_date").value;
