     if(secu_type == '' || secu_detl == '' || valuer == '' || val_date == '' || secu_value_fsv == '' || secu_value_mkt == ''){
      alert('Row ' + i + ' in the Pledge Funds account grid has unfilled required fields');
       return false;
      }
    }
}  
