  <jscript type="javascript" meta=""><![CDATA[hiddenById("DYN_FORWARD");
hiddenById("DYN_FORWARD][bullet");


function hideColumn(gridName, fieldName) {
   var colNo = getGridField(gridName, 1, fieldName).parentNode.cellIndex;
   rows = document.getElementById(gridName).rows;
   for (i=0; i < rows.length; i ++) {
      rows[i].cells[colNo].style.display= "none" ;
   }
}

function addComment(){  
  var oldcount = getField("oldcomment").value;
  var newcount = getField("comments").value.length;
  var nam = getField("curr_user").value;
  if(newcount != oldcount){
    var new_com = nam + ": " + getField("comments").value + "\n";
    getField("comments").value = new_com;
    getField("oldcomment").value = getField("comments").value.length;   
  }
}

function checkEmailDist() {
  var nRows = Number_Rows_Grid("to_email", "name_to");
  var checkEmail = true;
 
  for (var i = 1; i <= nRows; i++) {  
    if (getGridField("to_email", i, "cc_email").value != "")
    {  
      if (getGridField("to_email", i, "cc_email").value.substring(0,3) == 'gh.'){        
        checkEmail = false;
      }       
    }
  }
  return checkEmail;
};


getField('submit').onclick = function(){
  if(!checkEmailDist()){
    alert('Email for distribution cannot be group email. Only individual email addresses allowed');
    return false;
  }
  
  if (confirm('Are you sure you want to Submit', 'confirmation') == false){    
    return false;    
  }
  else {
   addComment();
  }
}

getField('draft').onclick = function(){
  saveForm("");  
  alert('Saved as Draft');
};]]></jscript>
  <checkIE type="javascript" meta=""><![CDATA[function detectIE() {
  var ua = window.navigator.userAgent;

  var msie = ua.indexOf('MSIE ');
  if (msie > 0) {
    // IE 10 or older => return version number
    return parseInt(ua.substring(msie + 5, ua.indexOf('.', msie)), 10);
  }

  var trident = ua.indexOf('Trident/');
  if (trident > 0) {
    // IE 11 => return version number
    var rv = ua.indexOf('rv:');
    return parseInt(ua.substring(rv + 3, ua.indexOf('.', rv)), 10);
  }

  var edge = ua.indexOf('Edge/');
  if (edge > 0) {
    // Edge (IE 12+) => return version number
    return parseInt(ua.substring(edge + 5, ua.indexOf('.', edge)), 10);
  }

  // other browser
  return false;
}

var version = detectIE();

if (version !== false){
  alert("Please use Mozilla Firefox or Google Chrome");
  disable(getField("submit"));
}]]></checkIE>
