function addComment(){  
  var oldcount = getField("oldcomment").value;
  var newcount = getField("comments").value.length;
  var nam = getField("curr_user").value;
  if(newcount != oldcount){
    var new_com = nam + ": " + getField("comments").value + "\n";
    getField("comments").value = new_com;
    getField("oldcomment").value = getField("comments").value.length;   
  }
}

 
getField('approve_btn').onclick = function(){
  if (confirm('Are you sure you want to APPROVE') == true){
    getField('approve_flag').value = 1;
    addComment()
  }
  else return false;
}
  

  getField('reject_btn').onclick = function(){
    if (confirm('Are you sure you want to REJECT') == true){ 
      getField('approve_flag').value = 0;
      addComment()
    }
    else return false;
  };]]></jscript>
